mapper.update:
update t_real_order
set state = #{state}
where real_order_id = #{realOrderId}

select.if:
select * from t_real_order

  user_id = #{userId} and state = #{status}

    and (cas_no like CONCAT('%', #{searchKey, jdbcType=VARCHAR}, '%'))


order by
real_order_id asc

  , ${orderColumn} ${orderType}
